let botoes = document.querySelectorAll(".btn");for(botao of botoes){botao.addEventListener('click',function(e){let id = e.target.value;let paragrafo = document.querySelector(`#${id}`);paragrafo.style.display = "block";desativaParagrafos(paragrafo);})}function desativaParagrafos(atual){let paragrafos = document.querySelectorAll(".some");for(paragrafo of paragrafos){if(paragrafo.id != atual.id){paragrafo.style.display = "none";}}}let inicio = document.querySelector(`#suporte`);inicio.style.display = "block";